import React from "react";
import Header from "./components/Header"
import Footer from "./components/Footer"
import Items from "./components/items";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            orders: [],
            items: [
                {
                    id: 1,
                    title: 'Стул серый',
                    img: 'chair.jpg',
                    category: 'chairs',
                    desc: "Lorem Ipsum",
                    price: '49,99'
                },
                {
                    id: 2,
                    title: 'Стул',
                    img: 'chair2.jpg',
                    category: 'chairs',
                    desc: "Ipsum Lorem",
                    price: '59,99'
                },
                {
                    id: 3,
                    title: 'Стол',
                    img: 'table.jpg',
                    category: 'Tables',
                    desc: "Ipsum Lorem",
                    price: '39,99'
                },
                {
                    id: 4,
                    title: 'Стол',
                    img: 'table1.jpg',
                    category: 'Tables',
                    desc: "Ipsum Lorem",
                    price: '39,99'
                },
                {
                    id: 5,
                    title: 'Стол',
                    img: 'table1.jpg',
                    category: 'Tables',
                    desc: "Ipsum Lorem",
                    price: '39,99'
                },
                {
                    id: 6,
                    title: 'Стол',
                    img: 'table1.jpg',
                    category: 'Tables',
                    desc: "Ipsum Lorem",
                    price: '39,99'
                }
            ]
        }
        this.addToOrder = this.addToOrder.bind(this)
    }
    render() {
    return (
    <div className="wrapper">
      <Header/>
        <Items items={this.state.items} onAdd={this.addToOrder}/>
      <Footer/>
    </div>
  );
    }

    addToOrder(item) {
        this.setState({orders: [...this.state.orders, item]}, () => {
            console.log(this.state.orders)
        })
    }
}

export default App;
